import React, {useEffect, useState} from 'react';
import { Link, useParams } from 'react-router-dom';
import {
REMOVE_THOUGHT,
} from '../utils/actions';
import { useStoreContext } from '../utils/GlobalState';
import { useQuery } from '@apollo/client';
import { QUERY_USER } from '../utils/queries';
import { useMutation } from '@apollo/client';
import { idbPromise } from '../utils/helpers';

function OrderHistory() {

  const [state, dispatch] = useStoreContext();
  const { id } = useParams();
  console.log('req');
  const { data, loading  } = useQuery(QUERY_USER);

  console.log({data})
  const [user, setUser] = useState({user:{}, orders:[], thoughts:[]});

  useEffect(() => {
    console.log('eff')
    console.log({loading, data})
    if (data) {
    console.log({data});
      setUser(data.user);
      // user = data.user;
      // console.log(user);
    }
  }, [data, loading]);

  const removeThought = () => {
    dispatch({
      type: REMOVE_THOUGHT,
      _id: thought._id,
    });

    idbPromise('cart', 'delete', { ...thoughts });
  };

  return (
    <>
      <div className="container my-1">
        <Link to="/">‚Üê Back to Products</Link>

        {loading ? 'Loading...' : (data &&
          <>
            <h2>
              Order History for {user.firstName} {user.lastName}
            </h2>
            {user.orders.map((order) => (
              <div key={order._id} className="my-2">
                <h3>
                  {new Date(parseInt(order.purchaseDate)).toLocaleDateString()}
                </h3>
                <div className="flex-row">
                  {order.products.length && order.products.map(({ _id, image, name, price }, index) => (
                    <div key={index} className="card px-1 py-1">
                      <Link to={`/products/${_id}`}>
                        <img alt={name} src={`/images/${image}`} />
                        <p>{name}</p>
                      </Link>
                      <div>
                        <span>${price}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
            <h2>
              Post History for {user.firstName}
            </h2>
            {user.thoughts.map((thought) => (
              <div key={thought._id} className="card mb-3">
              <h4 className="card-header bg-primary text-light p-2 m-0">
                  <Link
                    className="text-light"
                    to={`/thoughts/${thought._id}`}
                  >
                    {thought.thoughtAuthor} <br />
                  </Link>
              </h4>
              <div className="card-body bg-light p-2">
                <p>{thought.thoughtText}</p>
                <button
              disabled={!thought.find((id) => id._id === thought._id)}
              onClick={removeThought}
            >Delete
            </button>
              </div>


              <Link
                className="btn btn-primary btn-block btn-squared"
                to={`/thoughts/${thought._id}`}
              >
                Join the discussion.
              </Link>
            </div>
            ))}
          </>
        )}
      </div>
    </>
  );
}

export default OrderHistory;
